    std::map<std::string, poseidon::Ciphertext> BackendServer::compute(poseidon::Ciphertext &ctxt) {
        std::string url_compute = "127.0.0.1:" + port_web_default + "/chip/setCompute";
        cinatra::coro_http_client client{};
        nlohmann::json json;

        chrono::time_point<chrono::system_clock> start_ = chrono::high_resolution_clock::now();

        size_t i = 0;
        size_t database_size = database_.size();
        json["database_size"] = database_size;
        json["is_done"] = false;
        json["face_id_size"] = face_id_size_;

        std::map<std::string, poseidon::Ciphertext> result;

        for (auto &[id, ptxt] : database_) {
            poseidon::Ciphertext ctxt_temp;
            evaluator_->multiply_plain(ctxt, ptxt, ctxt_temp);
            evaluator_->rescale(ctxt_temp, ctxt_temp);
            evaluator_->drop_modulus_to_next(ctxt_temp, ctxt_temp);

            // rotate
            evaluator_->read(ctxt_temp);
            poseidon::Ciphertext ctxt_result = ctxt_temp;
            for (auto i = 1; i < 1024; i <<= 1) {
                ctxt_temp = ctxt_result;
                evaluator_->rotate(ctxt_temp, ctxt_temp, i, galois_key_);
                evaluator_->add(ctxt_result, ctxt_temp, ctxt_result);
                evaluator_->read(ctxt_result);
            }

            result.insert({id, ctxt_result});
            i ++;
            json["completed_size"] = i;
            auto computation = chrono::duration_cast<chrono::microseconds>(
                                   chrono::high_resolution_clock::now() - start_)
                                   .count() / 1000;
            json["computation_time"] = computation;
            json["single_ciph_computation_time"] = computation / i;
            client.post(url_compute, json.dump(), cinatra::req_content_type::json);
        }
        face_id_size_ += 1;
        json["face_id_size"] = face_id_size_;
        json["is_done"] = true;
        client.post(url_compute, json.dump(), cinatra::req_content_type::json);

        return result;
    }

    nlohmann::json BackendServer::handler_set_galois_key(nlohmann::json& json) {
        auto cstream = json["bytes"];
        if (!cstream.is_array()) {
            return generate_json(-1, "json format error: is not binary", get_timestamp(), nlohmann::json{});
        }

        std::stringstream ss = json_to_stream(cstream);
        galois_key_.load(context_, ss);

        return generate_json(0, "", get_timestamp(), nlohmann::json{"set galois key success"});
    }

    nlohmann::json BackendServer::serialize(const std::map<std::string, poseidon::Ciphertext> &ctxt_vec) {
        nlohmann::json json;

        for (auto& [id, ctxt] : ctxt_vec) {
            std::stringstream ss(std::ios_base::binary | std::ios_base::in | std::ios_base::out);
            ctxt.save(ss);
            json[id] = stream_to_json(ss);
        }

        return json;
    }

    nlohmann::json BackendServer::handler_ciphertext(nlohmann::json& json) {
        auto cstream = json["bytes"];
        if (!cstream.is_array()) {
            return generate_json(-1, "json format error: is not binary", get_timestamp(), nlohmann::json{});
        }

        std::stringstream ss = json_to_stream(cstream);
        poseidon::Ciphertext ctxt;
        ctxt.load(context_, ss);

        // compute the inner product of ciphertext and database
        return serialize(compute(ctxt));
    }

