    nlohmann::json BackendServer::handler_test_ciphertext(nlohmann::json &json) {
        auto cstream = json["bytes"];
        std::stringstream ss = json_to_stream(cstream);
        poseidon::Ciphertext ctxt;
        ctxt.load(context_, ss);

        std::stringstream ss_out(std::ios_base::binary | std::ios_base::in | std::ios_base::out);
        ctxt.save(ss_out);
        return stream_to_json(ss_out);
    }

